    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{ product.name }} - Ma Boutique{% endblock %}

{% block content %}

    <div class=\"row\">
        <div class=\"col-md-5\">
            <img src=\"{{ product.illustration }}\" alt=\"{{ product.name }}\" class=\"img-fluid\">
        </div>
        <div class=\"col-md-7 my-auto\">
            <h3>{{ product.name }}</h3>
            <p>{{ product.subtitle }}</p>
            <span class=\"product-page-price\">{{ (product.price / 100)|number_format(2, ',', '.') }} €</span>
            <hr>
            <p>
                {{ product.description }}
            </p>
            <!-- Je passe en paramètre la route \"add\" et l'id spécifique au produit --->
            <a href=\"{{ path('add_to_cart', {'id': product.id }) }}\" class=\"btn btn-primary\">Ajouter au panier</a>

        </div>
    </div>

    <hr>

    <h3>Nos meilleures ventes</h3>
    <p>Découvrez les articles les plus vendus</p>
    <div class=\"row\">
        {% for product in products %}
            <div class=\"col-md-3 d-flex justify-content-center\">
                {% include 'product/single_product.html.twig' %}
            </div>
        {% endfor %}
    </div>


{% endblock %}
", "product/show.html.twig", "C:\\Users\\oumayma\\PhpstormProjects\\blogsymfony\\ma-boutique\\templates\\product\\show.html.twig");
